import { gql, useQuery } from "@apollo/client";
import { useRouter } from "next/router";

const OBTENER_CLIENTES = gql`
    query ObtenerClientesVendedor {
        obtenerClientesVendedor {
            id
            nombre
            email
        }
    }
`;

const Index = () => {
    const router = useRouter();
    const { data, loading, error } = useQuery(OBTENER_CLIENTES);

    if (loading) return <p>Cargando...</p>;

    if (error) {
        console.error("Error en la consulta:", error.message);
        return <p>Error al cargar los datos: {error.message}</p>;
    }

    if (!data || !data.obtenerClientesVendedor) {
        console.warn("No se encontraron clientes. Redirigiendo al login.");
        router.push("/login");
        return null; // Evita renderizar contenido adicional
    }

    return (
        <div>
            <h1>Clientes</h1>
            <ul>
                {data.obtenerClientesVendedor.map((cliente) => (
                    <li key={cliente.id}>{cliente.nombre}</li>
                ))}
            </ul>
        </div>
    );
};

export default Index;
